import React from "react"
import { Box, color, radius, Icon, Flex } from "./Style"
import {
  SectionHeading,
  RowMaxWidth,
  ResponsiveGrid,
  RowPadding,
} from "./Layout"

export default () => (
  <RowPadding>
    <RowMaxWidth>
      <SectionHeading>套餐价格</SectionHeading>
      <ResponsiveGrid column={[1, 3]}>
        {priceData.map((item, i) => (
          <Box
            position={"relative"}
            key={i}
            bg={color.bianca}
            r={radius.lg}
            p={2}
          >
            {item.recommend && (
              <Flex
                position={"absolute"}
                top={0}
                transform={{ y: "-50%" }}
                bg={color.yellow}
                color={color.white}
                p={0.75}
                r={radius.pill}
                weight={"bold"}
              >
                <Icon source={iconPen} mr={0.25} />
                最热门
              </Flex>
            )}

            <Box scale={2} weight={"bold"} paragraph my={0.25}>
              {item.name}
            </Box>
            <Box scale={1} paragraph o={0.75}>
              {item.price}
            </Box>

            <Box position={"relative"}>
              <Box position={"absolute"} top={-1.75} right={0}>
                <item.illustration />
              </Box>
            </Box>

            <Box mt={7}>
              {item.benefit.map((sentence, sentenceOrder) => (
                <Box key={sentenceOrder} bb={color.divider} py={0.75}>
                  <Box o={0.75}>
                    <Box as="span" mr={0.75}>
                      ○
                    </Box>
                    {sentence}
                  </Box>
                </Box>
              ))}
            </Box>
          </Box>
        ))}
      </ResponsiveGrid>
    </RowMaxWidth>
  </RowPadding>
)

